sysauto_script.script:
//creare variabile tempo e data
var ora = new GlideDateTime();

//query sul database delle NeedIt Task confrotontando la data di scadenza con quella di corrente, non prendiamo i closed complete

//quando usi gliderecord inserire tabella da guardare
var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');

//procedi con le query
overdueNITasks.addQuery('due_date', '<=', ora);
overdueNITasks.addQuery('state','!=', 3);

//esecuzione query
overdueNITasks.query();

//scriviamo un messaggio di log 
while(overdueNITasks.next()){
	gs.info("Overdue NeedIt Task record = " + overdueNITasks.number);
	gs.eventQueue('x_58872_needit.overdueNeedItTask', overdueNITasks, overdueNITasks.number, gs.getUserName());
}
